import React, { useState } from "react";
import "./Food.css";

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);  // New state for loading

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    } else {
      alert("No file selected. Please choose an image.");
    }
  };

  const handleCameraCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const videoElement = document.createElement("video");
      videoElement.srcObject = stream;
      videoElement.play();

      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");

      videoElement.addEventListener("canplay", () => {
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
        canvas.toBlob(
          (blob) => {
            setImage(blob);
            setImagePreview(canvas.toDataURL("image/jpeg"));
            stream.getTracks().forEach((track) => track.stop());
          },
          "image/jpeg"
        );
      });
    } catch (error) {
      console.error("Error accessing camera:", error);
      alert("Unable to access camera. Please check permissions.");
    }
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    setIsLoading(true);  // Start loading animation

    try {
      // Get location information
      const location = await getLocation();
      const { latitude, longitude } = location.coords;
      const city = await getCityFromCoordinates(latitude, longitude);

      // Upload the image to Cloudinary
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", "food123");

      const uploadResponse = await fetch(
        `https://api.cloudinary.com/v1_1/dmf3jzijx/image/upload`,
        {
          method: "POST",
          body: formData,
        }
      );

      if (!uploadResponse.ok) {
        throw new Error("Image upload failed. Unable to create a valid link.");
      }

      const uploadData = await uploadResponse.json();
      const imageUrl = uploadData.secure_url;
      if (!imageUrl || !imageUrl.startsWith("http")) {
        throw new Error("Invalid image URL generated. Please try again.");
      }

      // Prepare JSON payload for FastAPI
      const payload = {
        image: imageUrl,
        location: city,
      };

      // Call FastAPI for food analysis
      const response = await fetch(
        "https://foodapi-sa0l.onrender.com/ai_ImgAnalyser",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        }
      );

      if (!response.ok) {
        throw new Error("Analysis failed. Please try again.");
      }

      const data = await response.json();
      if (!data.response || !data.meta_data) {
        throw new Error("Analysis failed. No result returned.");
      }

      const {
        category,
        type,
        count,
        temperature,
        humidity,
        exp_date,
      } = data.meta_data;

      const summary = {
        category,
        type,
        count,
        temperature,
        humidity,
        exp_date,
        analysis: data.response,
      };

      setResult(summary);
      setError(null);
    } catch (error) {
      console.error("Error during upload or analysis:", error);
      setError("An error occurred. Please try again.");
      setResult(null);
    } finally {
      setIsLoading(false);  // Stop loading animation
    }
  };

  const resetAnalysis = () => {
    setImage(null);
    setImagePreview(null);
    setResult(null);
    setError(null);
  };

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. Our AI ensures
        quality by detecting signs of freshness or decay.
      </p>
      <div className="file-input">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          style={{ width: "100%" }}
        />
      </div>
      <div>
        <button onClick={handleCameraCapture} className="analyze-button">
          Capture from Camera
        </button>
      </div>
      {isLoading && (
        <div className="dot-loader">
          <span></span>
          <span></span>
          <span></span>
        </div>
      )}
      <div>
        <button
          className="analyze-button"
          onClick={analyzeFood}
          disabled={isLoading}
        >
          Analyze
        </button>
        <button className="reset-button" onClick={resetAnalysis}>
          Reset
        </button>
      </div>

      {imagePreview && (
        <div>
          <img
            src={imagePreview}
            alt="Preview"
            className="image-preview-large"
          />
        </div>
      )}

      {result && (
        <div>
          <h3>Analysis Result:</h3>
          <p>Category: {result.category}</p>
          <p>Type: {result.type}</p>
          <p>Count: {result.count}</p>
          <p>Temperature: {result.temperature}</p>
          <p>Humidity: {result.humidity}</p>
          <p>Expiration Date: {result.exp_date}</p>
          <p>Analysis: {result.analysis}</p>
        </div>
      )}

      {error && <p>{error}</p>}
    </div>
  );
};

export default Food;
