import React, { useState } from "react";
import "./Food.css";

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);  // Track loading state

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    } else {
      alert("No file selected. Please choose an image.");
    }
  };

  const handleCameraCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const videoElement = document.createElement("video");
      videoElement.srcObject = stream;
      videoElement.play();

      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");

      videoElement.addEventListener("canplay", () => {
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
        canvas.toBlob(
          (blob) => {
            setImage(blob);
            setImagePreview(canvas.toDataURL("image/jpeg"));
            stream.getTracks().forEach((track) => track.stop());
          },
          "image/jpeg"
        );
      });
    } catch (error) {
      console.error("Error accessing camera:", error);
      alert("Unable to access camera. Please check permissions.");
    }
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    try {
      setIsLoading(true);  // Start loading

      // Simulating analysis (replace with actual code)
      setTimeout(async () => {
        // Place your API call or analysis logic here
        const fakeResult = {
          category: "Fruit",
          type: "Apple",
          count: 10,
          temperature: "18Â°C",
          humidity: "60%",
          exp_date: "2024-01-01",
          analysis: "Fresh and healthy.",
        };

        setResult(fakeResult);
        setIsLoading(false);  // End loading
      }, 3000);  // Simulating a 3-second delay for loading
    } catch (error) {
      setError("An error occurred while analyzing the food.");
      setIsLoading(false);  // End loading in case of an error
    }
  };

  const resetAnalysis = () => {
    setImage(null);
    setImagePreview(null);
    setResult(null);
    setError(null);
    setIsLoading(false);  // Reset loading state
  };

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. Our AI ensures
        quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input
          className="file-input"
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
        <button className="analyze-button" onClick={handleCameraCapture}>
          ð· Capture from Camera
        </button>
      </div>
      <button className="analyze-button analyze-large" onClick={analyzeFood} disabled={isLoading}>
        ð Analyze
      </button>

      {/* Show the dot loader when loading */}
      {isLoading && (
        <div className="dot-loader">
          <span></span>
          <span></span>
          <span></span>
        </div>
      )}

      {error && <p className="error-message">{error}</p>}
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img
              className="image-preview-large"
              src={imagePreview}
              alt="Analyzed food"
            />
          </div>
          <div className="result-container">
            <p className="result-text">ð§ª Analysis Summary:</p>
            <ul className="result-details">
              <li>ð Category: {result.category}</li>
              <li>ð Type: {result.type}</li>
              <li>ð¢ Count: {result.count}</li>
              <li>ð¡ï¸ Temperature: {result.temperature}</li>
              <li>ð§ Humidity: {result.humidity}</li>
              <li>ð Estimated Expiry: {result.exp_date}</li>
            </ul>
          </div>
        </div>
      )}
      <button className="reset-button" onClick={resetAnalysis}>
        ð Reset
      </button>
    </div>
  );
};

export default Food;
