import React, { useState } from 'react';
import './Chat.css';

const Chat = () => {
  const [messages, setMessages] = useState([
    { type: 'user', text: 'Hello, what can you do?' },
    { type: 'bot', text: 'I can help manage inventory and analyze food spoilage.' }
  ]);

  const [input, setInput] = useState('');

  const handleSendMessage = () => {
    if (input.trim()) {
      setMessages([...messages, { type: 'user', text: input }]);
      setInput('');
      setTimeout(() => {
        setMessages((prev) => [
          ...prev,
          { type: 'bot', text: 'Processing your request...' }
        ]);
      }, 1000);
    }
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <h1>AI Inventory Manager</h1>
      </header>
      <div className="chat-table">
        <table>
          <thead>
            <tr>
              <th>User</th>
              <th>AI Response</th>
            </tr>
          </thead>
          <tbody>
            {messages.map((msg, index) => (
              <tr key={index} className={msg.type === 'user' ? 'user-row' : 'bot-row'}>
                <td>{msg.type === 'user' ? msg.text : '-'}</td>
                <td>{msg.type === 'bot' ? msg.text : '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="chat-input-area">
        <input
          type="text"
          className="chat-input"
          value={input}
          placeholder="Type your message here..."
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
