import React from "react";
import "./Inventory.css";

const Inventory = () => {
  // Updated inventory data
  const inventory = [
    {
      category: "Fruits",
      items: [
        { name: "Apple", quantity: "3", expiry: "2025-01-13" },
        { name: "Banana", quantity: "2", expiry: "2025-01-03" },
      ],
    },
    {
      category: "Vegetables",
      items: [
        { name: "Tomato", quantity: "5", expiry: "2025-01-09" },
        { name: "Onion", quantity: "2", expiry: "2025-02-14" },
      ],
    },
    {
      category: "Dairy",
      items: [], // Empty Dairy section
    },
    {
      category: "Others",
      items: [], // Empty Others section
    },
  ];

  return (
    <div className="inventory-container">
      <div className="inventory-header">
        <h1>INVENTORY</h1>
      </div>
      <div className="inventory-content">
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Type of Food</th>
              <th>Food Item</th>
              <th>Quantity</th>
              <th>Expiry Date</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((category) =>
              category.items.length > 0 ? (
                category.items.map((item, index) => (
                  <tr key={`${category.category}-${index}`}>
                    {index === 0 && (
                      <td rowSpan={category.items.length}>{category.category}</td>
                    )}
                    <td>{item.name}</td>
                    <td>{item.quantity}</td>
                    <td>{item.expiry}</td>
                  </tr>
                ))
              ) : (
                <tr key={category.category}>
                  <td rowSpan="1">{category.category}</td>
                  <td colSpan="3">[empty]</td>
                </tr>
              )
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventory;
