import React, { useState } from "react";
import "./AIManager.css";

const AIManager = () => {
  const [input, setInput] = useState("");
  const [response, setResponse] = useState("Hi! It's your Inventory Manager here.");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate AI response (Replace this with actual AI logic or API call)
    if (input.toLowerCase().includes("stock")) {
      setResponse("Your stock levels are optimal.");
    } else {
      setResponse("I am here to assist with inventory management.");
    }
    setInput("");
  };

  return (
    <div className="ai-manager">
      <div className="ai-header">
        <h2>INVENTORY</h2>
      </div>

      <div className="ai-content">
        <p>{response}</p>
      </div>

      <form onSubmit={handleSubmit} className="ai-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask your question"
          required
        />
        <button type="submit">
          <span className="dot"></span>
        </button>
      </form>
    </div>
  );
};

export default AIManager;
