import React, { useState } from "react";
import './Food.css';

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleCameraCapture = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    const videoElement = document.createElement('video');
    videoElement.srcObject = stream;
    videoElement.play();

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    videoElement.addEventListener('canplay', () => {
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
      canvas.toBlob((blob) => {
        setImage(blob);
        setImagePreview(canvas.toDataURL('image/jpeg'));
        stream.getTracks().forEach(track => track.stop());
      }, 'image/jpeg');
    });
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", image);

    try {
      const response = await fetch("https://your-api-endpoint/analyze-food", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult({
        status: data.result, // E.g., "Fresh apple detected. No spoilage detected."
        daysUntilSpoilage: data.daysUntilSpoilage, // E.g., "3 days until spoilage."
      });
    } catch (error) {
      console.error("Error analyzing food:", error);
      setResult({
        status: "Error analyzing food. Please try again.",
        daysUntilSpoilage: null,
      });
    }
  };

  return (
    <div className="food-container">
      <h1 className="food-title">AI Food Analyzer</h1>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. 
        Our AI ensures quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
        <button className="analyze-button" onClick={handleCameraCapture}>üì∑ Capture from Camera</button>
      </div>
      <button className="analyze-button analyze-large" onClick={analyzeFood}>üîç Analyze</button>
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img className="image-preview-large" src={imagePreview} alt="Analyzed food" />
          </div>
          <div className="result-container">
            <p className="result-text">üß™ Result: {result.status}</p>
            {result.daysUntilSpoilage && (
              <p className="result-text">‚åõ Days Until Spoilage: {result.daysUntilSpoilage}</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Food;
