import React from "react";
import "./Inventory.css";

const Inventory = () => {
  const inventory = [
    {
      id: 1,
      category: "Fruits",
      items: [
        { name: "Banana", quantity: "5 Bunches", expiry: "2024-01-15" },
        { name: "Apple", quantity: "10", expiry: "2024-01-10" },
        { name: "Guava", quantity: "10", expiry: "2024-01-12" },
      ],
    },
    {
      id: 2,
      category: "Dairy",
      items: [
        { name: "Milk", quantity: "5 Packs", expiry: "2023-12-30" },
        { name: "Cheese", quantity: "3 Packs", expiry: "2024-01-05" },
        { name: "Curd", quantity: "2 Packs", expiry: "2023-12-28" },
      ],
    },
    {
      id: 3,
      category: "Vegetables",
      items: [
        { name: "Onion", quantity: "3 kg", expiry: "2024-01-20" },
        { name: "Cabbage", quantity: "3 kg", expiry: "2024-01-18" },
      ],
    },
    {
      id: 4,
      category: "Others",
      items: [
        { name: "Chips", quantity: "3 Packs", expiry: "2025-01-01" },
        { name: "Oil", quantity: "3 Packs", expiry: "2024-12-31" },
      ],
    },
  ];

  return (
    <div className="inventory-container">
      <div className="inventory-header">
        <h1>Inventory Dashboard</h1>
      </div>
      <div className="inventory-content">
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Type of Food</th>
              <th>Number of Types</th>
              <th>Quantity/Bunches</th>
              <th>Expiry Date</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((category) =>
              category.items.map((item, index) => (
                <tr key={`${category.id}-${index}`}>
                  {index === 0 && (
                    <td rowSpan={category.items.length} className="category-cell">
                      {category.category}
                    </td>
                  )}
                  <td>{item.name}</td>
                  <td>{item.quantity}</td>
                  <td>{item.expiry}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventory;
