import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getDatabase, ref, get } from "firebase/database";
import "./Home.css";

// Initialize Firebase (you should have already initialized Firebase in your main app file)
import { initializeApp } from "firebase/app";

const firebaseConfig = {
  apiKey: "AIzaSyB-L2pj_4XVH9y7VBYOSAsTsT8p4cveHZY",
  authDomain: "foodai-7ebf0.firebaseapp.com",
  databaseURL: "https://foodai-7ebf0-default-rtdb.firebaseio.com",
  projectId: "foodai-7ebf0",
  storageBucket: "foodai-7ebf0.firebasestorage.app",
  messagingSenderId: "1802738846",
  appId: "1:1802738846:web:f6985b95b8487ce3c7ef8f",
  measurementId: "G-QJWXCZ7Y56"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

const Home = () => {
  const navigate = useNavigate();

  // State to hold the inventory data
  const [inventoryData, setInventoryData] = useState({
    totalItems: 0,
    categories: {
      others: 0,
      fruits: 0,
      dairy: 0,
      vegetables: 0
    }
  });

  // Function to fetch inventory data from Firebase
  const fetchInventoryData = async () => {
    try {
      const dbRef = ref(database);
      const snapshot = await get(dbRef);
      if (snapshot.exists()) {
        const data = snapshot.val();
        let totalItems = 0;
        let categories = {
          others: 0,
          fruits: 0,
          dairy: 0,
          vegetables: 0
        };

        // Loop through the categories in Firebase and calculate the totals
        for (const category in data) {
          for (const type in data[category]) {
            totalItems += data[category][type].count || 0;
            // Update the category count
            if (category === "others") categories.others += data[category][type].count || 0;
            if (category === "fruits") categories.fruits += data[category][type].count || 0;
            if (category === "dairy") categories.dairy += data[category][type].count || 0;
            if (category === "vegetables") categories.vegetables += data[category][type].count || 0;
          }
        }

        // Set the inventory data to state
        setInventoryData({
          totalItems,
          categories
        });
      }
    } catch (error) {
      console.error("Error fetching inventory data from Firebase:", error);
    }
  };

  // Fetch inventory data when the component is mounted
  useEffect(() => {
    fetchInventoryData();
  }, []);

  // Redirect functions
  const handleRedirectToInventory = () => {
    navigate("/inventory");
  };

  const handleRedirectToFoodAnalyzer = () => {
    navigate("/food");
  };

  const handleRedirectToAIManager = () => {
    navigate("/ai-manager");
  };

  return (
    <div className="home">
      <header className="home-header">
        <h1>INVENTORY MANAGEMENT SYSTEM</h1>
        <div className="user-icon">A</div>
      </header>

      <div className="inventory-summary button-card" onClick={handleRedirectToInventory}>
        <h2>Inventory</h2>
        <div className="circle-chart">
          <p className="item-count">{inventoryData.totalItems}</p>
          <p className="item-label">Items</p>
        </div>
        <div className="legend">
          <div>
            <span className="dot others"></span> Others ({inventoryData.categories.others})
          </div>
          <div>
            <span className="dot fruits"></span> Fruits ({inventoryData.categories.fruits})
          </div>
          <div>
            <span className="dot dairy"></span> Dairy ({inventoryData.categories.dairy})
          </div>
          <div>
            <span className="dot vegetables"></span> Vegetables ({inventoryData.categories.vegetables})
          </div>
        </div>
      </div>

      <div className="additional-info">
        <div className="info-box button-card" onClick={handleRedirectToFoodAnalyzer}>
          <h3>AI Food Analyzer</h3>
          <img src="https://img.icons8.com/color/64/camera.png" alt="Sensor Data" />
        </div>

        <div className="info-box button-card" onClick={handleRedirectToAIManager}>
          <h3>AI INVENTORY MANAGER</h3>
          <img src="https://img.icons8.com/color/64/artificial-intelligence.png" alt="AI Inventory Manager" />
        </div>
      </div>
    </div>
  );
};

export default Home;
