import React, { useState } from "react";
import './Food.css';

const Food = () => {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  const handleCameraCapture = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    const videoElement = document.createElement('video');
    videoElement.srcObject = stream;
    videoElement.play();

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    videoElement.addEventListener('canplay', () => {
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
      canvas.toBlob((blob) => {
        setImage(blob);
        stream.getTracks().forEach(track => track.stop());
      }, 'image/jpeg');
    });
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", image);

    try {
      const response = await fetch("https://your-api-endpoint/analyze-food", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data.result);
    } catch (error) {
      console.error("Error analyzing food:", error);
      setResult("Error analyzing food. Please try again.");
    }
  };

  return (
    <div className="food-container">
      <h1 className="food-title">AI Food Analyzer</h1>
      <p className="food-description">Upload or capture a photo to analyze your food items.</p>
      <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
      <button className="analyze-button" onClick={analyzeFood}>Analyze</button>
      <button className="analyze-button" onClick={handleCameraCapture}>Capture from Camera</button>
      {result && <p className="result-text">Result: {result}</p>}
    </div>
  );
};

export default Food;
