import React, { useState } from "react";
import { initializeApp } from "firebase/app";
import { getStorage, ref, uploadBytes, getDownloadURL, deleteObject } from "firebase/storage";
import './Food.css';

// Firebase Configuration
const firebaseConfig = {
  apiKey: "AIzaSyB-L2pj_4XVH9y7VBYOSAsTsT8p4cveHZY",
  authDomain: "foodai-7ebf0.firebaseapp.com",
  databaseURL: "https://foodai-7ebf0-default-rtdb.firebaseio.com",
  projectId: "foodai-7ebf0",
  storageBucket: "foodai-7ebf0.appspot.com",
  messagingSenderId: "1802738846",
  appId: "1:1802738846:web:f6985b95b8487ce3c7ef8f",
  measurementId: "G-QJWXCZ7Y56"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const storage = getStorage(app);

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);
  const [uploadedImageUrl, setUploadedImageUrl] = useState(null);
  const [location, setLocation] = useState("Unknown");

  // Handle Image Upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Handle Camera Capture
  const handleCameraCapture = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    const videoElement = document.createElement('video');
    videoElement.srcObject = stream;
    videoElement.play();

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    videoElement.addEventListener('canplay', () => {
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
      canvas.toBlob((blob) => {
        setImage(blob);
        setImagePreview(canvas.toDataURL('image/jpeg'));
        stream.getTracks().forEach(track => track.stop());
      }, 'image/jpeg');
    });
  };

  // Fetch User Location
  const fetchLocation = async () => {
    try {
      const res = await fetch("https://ipapi.co/json/");
      const data = await res.json();
      setLocation(data.city || "Unknown");
    } catch (err) {
      console.error("Error fetching location:", err);
    }
  };

  // Upload Image to Firebase and Get URL
  const uploadImageToFirebase = async () => {
    if (!image) return null;
    const storageRef = ref(storage, `images/${Date.now()}_${image.name}`);
    await uploadBytes(storageRef, image);
    return getDownloadURL(storageRef);
  };

  // Analyze Food
  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    // Fetch location before analyzing
    await fetchLocation();

    try {
      const imageUrl = await uploadImageToFirebase();
      setUploadedImageUrl(imageUrl);

      const response = await fetch("https://foodapi-sa0l.onrender.com", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image_url: imageUrl, location }),
      });

      const data = await response.json();
      setResult({
        isExpired: data.response.includes("Expired") ? "Yes" : "No",
        daysUntilSpoilage: data.meta_data?.exp_date || "Unknown",
      });
    } catch (error) {
      console.error("Error analyzing food:", error);
      setResult({
        isExpired: "Error",
        daysUntilSpoilage: "Error",
      });
    }
  };

  // Reset All States
  const reset = async () => {
    setImage(null);
    setImagePreview(null);
    setResult(null);
    if (uploadedImageUrl) {
      try {
        const imageRef = ref(storage, uploadedImageUrl);
        await deleteObject(imageRef);
      } catch (error) {
        console.error("Error deleting image from Firebase:", error);
      }
    }
    setUploadedImageUrl(null);
  };

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. 
        Our AI ensures quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
        <button className="analyze-button" onClick={handleCameraCapture}>📷 Capture from Camera</button>
      </div>
      <button className="analyze-button analyze-large" onClick={analyzeFood}>🔍 Analyze</button>
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img className="image-preview-large" src={imagePreview} alt="Analyzed food" />
          </div>
          <div className="result-container">
            <p className="result-text">🧪 Is Expired: {result.isExpired}</p>
            <p className="result-text">⌛ Days Until Spoilage: {result.daysUntilSpoilage}</p>
          </div>
        </div>
      )}
      <button className="analyze-button analyze-large" onClick={reset}>🔄 Reset</button>
      <button className="analyze-button analyze-large">📦 Add to Inventory</button>
    </div>
  );
};

export default Food;
