import React from "react";
import "./Inventory.css";

const Inventory = () => {
  // Sample data matching the image
  const inventory = [
    {
      id: 1,
      category: "Fruits",
      items: [
        { name: "Banana", quantity: "5 Bunches" },
        { name: "Apple", quantity: "10" },
        { name: "Guava", quantity: "10" },
      ],
    },
    {
      id: 2,
      category: "Dairy",
      items: [
        { name: "Milk", quantity: "5 Packs" },
        { name: "Cheese", quantity: "3 Packs" },
        { name: "Curd", quantity: "2 Packs" },
      ],
    },
    {
      id: 3,
      category: "Vegetables",
      items: [
        { name: "Onion", quantity: "3 kg" },
        { name: "Cabbage", quantity: "3 kg" },
      ],
    },
    {
      id: 4,
      category: "Others",
      items: [
        { name: "Chips", quantity: "3 Packs" },
        { name: "Oil", quantity: "3 Packs" },
      ],
    },
  ];

  const renderCategory = (category) => {
    const colors = {
      Fruits: "fruits",
      Dairy: "dairy",
      Vegetables: "vegetables",
      Others: "others",
    };

    return (
      <div key={category.id} className="inventory-category">
        <div className="category-header">
          <span className={`dot ${colors[category.category] || "others"}`}></span>
          <h3>{category.category}</h3>
        </div>
        <ul className="category-list">
          {category.items.map((item, index) => (
            <li key={index}>
              {item.name} ---&gt; {item.quantity}
            </li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="inventory-container">
      <div className="inventory-header">
        <h1>INVENTORY</h1>
      </div>
      <div className="inventory-content">
        {inventory.map(renderCategory)}
      </div>
    </div>
  );
};

export default Inventory;
