import React, { useEffect, useState } from "react";
import "./Inventory.css";

// Firebase initialization
import firebase from "firebase/app";
import "firebase/database";

// Your Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyB-L2pj_4XVH9y7VBYOSAsTsT8p4cveHZY",
  authDomain: "foodai-7ebf0.firebaseapp.com",
  databaseURL: "https://foodai-7ebf0-default-rtdb.firebaseio.com",
  projectId: "foodai-7ebf0",
  storageBucket: "foodai-7ebf0.firebasestorage.app",
  messagingSenderId: "1802738846",
  appId: "1:1802738846:web:f6985b95b8487ce3c7ef8f",
  measurementId: "G-QJWXCZ7Y56"
};

// Initialize Firebase if not already initialized
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
} else {
  firebase.app(); // Use the existing app
}

const database = firebase.database();

const Inventory = () => {
  // State to store inventory data
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    // Fetch data from Firebase Realtime Database
    const inventoryRef = database.ref("/inventory"); // Assuming your data is under the "inventory" node

    inventoryRef.on("value", (snapshot) => {
      const data = snapshot.val();
      if (data) {
        // Map the data to an array of categories and items
        const formattedInventory = Object.keys(data).map((category) => ({
          category,
          items: Object.values(data[category]),
        }));
        setInventory(formattedInventory);
      }
    });

    // Clean up the listener on unmount
    return () => {
      inventoryRef.off();
    };
  }, []);

  return (
    <div className="inventory-container">
      <div className="inventory-header">
        <h1>INVENTORY</h1>
      </div>
      <div className="inventory-content">
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Type of Food</th>
              <th>Food Item</th>
              <th>Quantity</th>
              <th>Expiry Date</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((category) =>
              category.items.length > 0 ? (
                category.items.map((item, index) => (
                  <tr key={`${category.category}-${index}`}>
                    {index === 0 && (
                      <td rowSpan={category.items.length}>{category.category}</td>
                    )}
                    <td>{item.name}</td>
                    <td>{item.quantity}</td>
                    <td>{item.expiry}</td>
                  </tr>
                ))
              ) : (
                <tr key={category.category}>
                  <td rowSpan="1">{category.category}</td>
                  <td colSpan="3">[empty]</td>
                </tr>
              )
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inventory;
